useEffect(() => {
    const id ="100";
    let tokenStr = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6MTAwL吃1OH0.r92B_Sgm0DCGZ-Unc6esV5bticKyFvy98uiIFDm3hUs';
    axios
        .get(`https://sleeptrack.herokuapp.com/api/user/${id}`, { headers: {"Authorization" : `Bearer ${tokenStr}`} })
        .then(response => {
            setUser(response.data);
        })
        .catch(error => {
            console.error(error);
        });

}, []);

const ActiveUserDataPage = props => {
    return (
        <div>
            <p>{user}</p>
        </div>
    )

}

export default ActiveUserDataPage;

